import React, { Component } from "react";

import { BrowserRouter as Route, Link } from "react-router-dom";

// import Control_Risk from "./control_risk_list.component";

// fake data
var areas = [

	{name: 'SIPRO'},
	{name: 'Pulpa Textil'}

]
class Home extends Component {

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			group_name: 'Arauco Planta Valdivia',
			areas: areas,
		}

		// this.on_submit = this.on_submit.bind(this);

	};

	render() {

		console.log(this);

		return(

			<div className = 'container'>

				<h2>

					Grupo {this.state.group_name}

				</h2>

				<p> 

					Crear nueva area

				</p>
				<div className = 'table table-responsive'>

					<table className = 'table'>

						<thead>

							<tr>

								<th> 

									Numero

								</th>

								<th>

									Area

								</th>

							</tr>

						</thead>

						<tbody>

							{this.state.areas.map( (area, idx) =>

								<tr key = {idx}>
									<td> {idx + 1} </td>
									<td> {area.name} </td>

								</tr>
							)}

						</tbody>

					</table>

				</div>

			</div>

		);

	}

}

export default Home;
