import React, { Component } from "react";

import { BrowserRouter as Route, Link } from "react-router-dom";

// import Control_Risk from "./control_risk_list.component";

// make request to server
import axios from 'axios';

class Daily_Question extends Component {

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			// name: null,
			// password: null,
		}

		this.on_submit = this.on_submit.bind(this);

	};

	on_submit(calification) {

		console.log(calification);
		// console.log(this);

		// event.preventDefault();

		const calification_ = {

			calification: calification
		}

		// get request for get data
        axios.post('http://localhost:4000/add_daily_question', calification_)

        	// if ok
            .then(response => {

        		// console.log('Submit');
        		window.confirm("¡Gracias por hacernos saber!");
        		// this.props.history.push('/work_env_manag_home/dashboard_group/');

            })

            // if error
            .catch(function (error){

            	window.confirm('Ups, tuvimos un problema, ¡vuelve a intentarlo mas tarde!');
            	// dislpay error in console
                console.log(error);

            });

	};

	render() {

		console.log(this);

		return(

			<div className = 'container'>

				<h4>

					Pregunta diaria

				</h4>


	            <div className="form-group"> 

	                <label> ¿Como te sientes hoy día en tu área de trabajo? </label>

				</div>

                <div className = 'form-group'>

	                <button onClick = {() => this.on_submit(-1)} type="button" className ="btn btn-danger btn-block"> 

						Mal

					</button>

                    <input  type="submit"
	                        value = "Normal" 
	                        className="btn btn-warning btn-block"
                    />

                    <input  type="submit"
	                        value = "Bien" 
	                        className="btn btn-info btn-block"
                    />

	            </div>

			</div>

		);

	}

}

export default Daily_Question;
