import React, { Component } from "react";

import { BrowserRouter as Link } from "react-router-dom";

import {Line} from 'react-chartjs-2';
// import Control_Risk from "./control_risk_list.component";

// fake data
var messages = [

	{date: '19-03-2019', message: 'Mi jefe directo me grita cada vez que quiere pedirme algo, debería mejorar su trato conmigo'},
	{date: '22-03-2019', message: 'Mi jefe directo es muy prepotente en las reuniones que tenemos'}

]

// fake data
const data = {
  labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
  datasets: [
    {
      label: 'Estado de animo del area',
      fill: false,
      lineTension: 0.1,
      backgroundColor: 'rgba(75,192,192,0.4)',
      borderColor: 'rgba(75,192,192,1)',
      borderCapStyle: 'butt',
      borderDash: [],
      borderDashOffset: 0.0,
      borderJoinStyle: 'miter',
      pointBorderColor: 'rgba(75,192,192,1)',
      pointBackgroundColor: '#fff',
      pointBorderWidth: 1,
      pointHoverRadius: 5,
      pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      pointHoverBorderColor: 'rgba(220,220,220,1)',
      pointHoverBorderWidth: 2,
      pointRadius: 1,
      pointHitRadius: 10,
      data: [-0.9, -0.9, -0.5, -0.1, 0, 0.1, 0.3, 1, 1]
    }
  ]
};

// componentn itself
class Dashboard_Areas extends Component {

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			area_name: 'Arauco Planta Valdivia',
			messages: messages,
		}

		this.create_area_button = this.create_area_button.bind(this);

	};

	create_area_button() {

		this.props.history.push('/work_env_manag_home/create_new_area/');

	};

	render() {

		console.log(this);

		return(

			<div className = 'container'>

				<h2>

					Area {this.state.area_name}

				</h2>

				<div className = 'container'>

					<h5>

						Monitoreo de estado anímico

					</h5>

					<Line data={data} />

				</div>

				<div className = 'container'>

					<h5>

						Mensajes de trabajadores del área

					</h5>

					<div className = 'table table-responsive'>


						<table className = 'table'>

							<thead>

								<tr>

									<th> 

										Numero

									</th>

									<th>

										Fecha

									</th>

									<th>

										Mensaje

									</th>

								</tr>

							</thead>

							<tbody>

								{this.state.messages.map( (message, idx) =>

									<tr key = {idx}>

										<td> {idx + 1} </td>
										<td> {message.date} </td>
										<td> {message.message} </td>

									</tr>
								)}

							</tbody>

						</table>

					</div>

				</div>

			</div>

		);

	}

}

export default Dashboard_Areas;
