import React, { Component } from "react";

import { BrowserRouter as Link } from "react-router-dom";

// import Control_Risk from "./control_risk_list.component";

// fake data
var areas = [

	{name: 'SIPRO'},
	{name: 'Pulpa Textil'}

]
class Dashboard_Areas extends Component {

	// constructor
	constructor(props) {

		// constructur of parent
		super(props);

		// initial states
		this.state = {

			group_name: 'Arauco Planta Valdivia',
			areas: areas,
		}

		this.create_area_button = this.create_area_button.bind(this);

	};

	create_area_button() {

		this.props.history.push('/work_env_manag_home/create_new_area/');

	};

	render() {

		console.log(this);

		return(

			<div className = 'container'>

				<h2>

					Area {this.state.group_name}

				</h2>

				<p> 

					<button type="button" className ="btn btn-primary" onClick = {this.create_area_button}>Crear nueva area</button>

				</p>
				<div className = 'table table-responsive'>

					<table className = 'table'>

						<thead>

							<tr>

								<th> 

									Numero

								</th>

								<th>

									Area

								</th>

							</tr>

						</thead>

						<tbody>

							{this.state.areas.map( (area, idx) =>

								<tr key = {idx}>
									<td> {idx + 1} </td>
									<td> {area.name} </td>

								</tr>
							)}

						</tbody>

					</table>

				</div>

			</div>

		);

	}

}

export default Dashboard_Areas;
